import { Component, OnDestroy } from '@angular/core';
import { AddCategoryRequest } from '../models/add-category-request.model';
import { CategoryService } from '../services/category.service';
import { Subscription } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-category',
  templateUrl: './add-category.component.html',
  styleUrls: ['./add-category.component.css']
})
export class AddCategoryComponent implements OnDestroy  {

  model:AddCategoryRequest;
  private addCategorySubscription?: Subscription;


  constructor(private categoryService:CategoryService, private router : Router){
    this.model = {
      name: '',
      urlHandle:''
    };
  }


  onFormSubmit(){
    this.addCategorySubscription = this.categoryService.addCategory(this.model).subscribe({
      next: (response) => {
        this.router.navigateByUrl('/admin/categories')
      }

    })

  }
  ngOnDestroy(): void {
    this.addCategorySubscription?.unsubscribe();
  }

}
<div class="container" >
<h1 class="mt-3">add category</h1>
<form #form="ngForm" (ngSubmit)="onFormSubmit()">
    <div class="mt-2">
        <div class="mt-3">
            <label class="form-label">category name</label>
            <input type="text" id="categoryName" class="form-control" name="categoryName"
            [(ngModel)]="model.name"
            >
        </div>
        <div class="mt-3">
            <label class="form-label">Category url handler</label>
            <input type="text" id="categoryUrlHandle" class="form-control" name="categoryUrlHandle"
            [(ngModel)]="model.urlHandle"
            >
        </div>
        <div class="mt-3">
            <button type="submit" class="btn btn-primary">save</button>

        </div>
    </div>
</form>
</div>>
