import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { CategoryService } from '../services/category.service';
import { Category } from '../models/category.model';
import { UpdatecategoryRequest } from '../models/update-category-request.model';

@Component({
  selector: 'app-edit-category',
  templateUrl: './edit-category.component.html',
  styleUrls: ['./edit-category.component.css']
})
export class EditCategoryComponent implements OnInit, OnDestroy {


  id:string | null = null;
  paramSubscription? : Subscription;
  category?: Category;
  updateCategorySubscription? : Subscription;

  constructor(private route:ActivatedRoute, private categoryService: CategoryService, private router: Router){

  }
  ngOnInit(): void {
    this.paramSubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id');

        if(this.id){
          this.categoryService.getCategoryById(this.id).subscribe({
            next:(response) => {
              this.category =  response
            }
          })
        }
      }
    })
  }


  onSubmit():void{
    const updateCategoryRequest: UpdatecategoryRequest ={
      name: this.category?.name ?? '',
      urlHandle: this.category?.urlHandle ?? ''
    }
    if(this.id){

      this.updateCategorySubscription = this.categoryService.UpdateCategory(this.id, updateCategoryRequest).subscribe({
        next: (response) => {
          this.router.navigateByUrl('/admin/categories');
        }
      });
    }

  }


  ngOnDestroy(): void {
    this.paramSubscription?.unsubscribe();
    this.updateCategorySubscription?.unsubscribe();
  }

}
<div class="container">
    <h1 class="mt-3">edit category   </h1>

<ng-container *ngIf="category;else notFound">
<form #form="ngForm" (ngSubmit)="onSubmit()">
    <div class="mt-2">
        <div class="mt-3">
            <label class="form-label" for="categoryId">category id</label>
            <input type="text" disabled
            id="categoryId"
            class="form-control"
            name="categoryId"
            value="{{category.id}}">

        </div>

        <div class="mt-3">
            <label class="form-label" for="categoryName">category name</label>
            <input type="text"  
            id="categoryName"
            class="form-control"
            name="categoryName"
            [(ngModel)]="category.name">

        </div>

        <div class="mt-3">
            <label class="form-label" for="categoryUrlHandle">category Url Handle</label>
            <input type="text" 
            id="categoryUrlHandle"
            class="form-control"
            name="categoryUrlHandle"
            [(ngModel)]="category.urlHandle">

        </div>
    </div>
    <div class="mt-3">
        <button type="submit" class="btn btn-primary">save</button>

    </div>
</form>


</ng-container>
<ng-template #notFound>
    <div class="alert alert-warning" role="alert"> category not found</div>
</ng-template>
</div>
