import { Component } from '@angular/core';
import { AddCategoryRequest } from '../models/add-category-request';

@Component({
  selector: 'app-add-category',
  templateUrl: './add-category.component.html',
  styleUrls: ['./add-category.component.css']
})
export class AddCategoryComponent {

  model:AddCategoryRequest;
  constructor(){
    this.model = {
      name: '',
      urlHandler:''
    };
  }

  onFormSubmit(){
    console.log(this.model);

  }
}
<div class="container" >
<h1 class="mt-3">add category</h1>
<form #form="ngForm" (ngSubmit)="onFormSubmit()">
    <div class="mt-2">
        <div class="mt-3">
            <label class="form-label">category name</label>
            <input type="text" id="categoryName" class="form-control" name="categoryName"
            [(ngModel)]="model.name"
            >
        </div>
        <div class="mt-3">
            <label class="form-label">Category url handler</label>
            <input type="text" id="categoryUrlHandler" class="form-control" name="categoryUrlHandler"
            [(ngModel)]="model.urlHandler"
            >
        </div>
        <div class="mt-3">
            <button type="submit" class="btn btn-primary">save</button>

        </div>
    </div>
</form>
</div>>
